public class Arithmetic {
    public static void main(String[] args) {
        int a = 10;
        int b = 3;

        System.out.println("Addition: " + (a + b));
        System.out.println("Subtraction: " + (a - b));
        System.out.println("Multiplication: " + (a * b));
        System.out.println("Division: " + (a / b));
        System.out.println("Modulus: " + (a % b));

        double c = 10.0;
        System.out.println("Decimal Division: " + (c / b));
    }
}

public class Comparison {
    public static void main(String[] args) {
        int age = 20;
        int minAge = 18;

        System.out.println("Equal: " + (age == minAge));
        System.out.println("Not Equal: " + (age != minAge));
        System.out.println("Greater: " + (age > minAge));
        System.out.println("Less: " + (age < minAge));
        System.out.println("Greater or Equal: " + (age >= minAge));
        System.out.println("Less or Equal: " + (age <= minAge));
    }
}

public class Logical {
    public static void main(String[] args) {
        int age = 20;
        boolean hasLicense = true;
        boolean hasInsurance = false;

        boolean canDrive = age >= 18 && hasLicense;
        System.out.println("Can drive: " + canDrive);

        boolean canRent = age >= 21 || hasInsurance;
        System.out.println("Can rent: " + canRent);

        boolean needsInsurance = !hasInsurance;
        System.out.println("Needs insurance: " + needsInsurance);
    }
}

