public class StringOps {
    public static void main(String[] args) {
        String text = "Hello World";

        System.out.println("Length: " + text.length());
        System.out.println("First char: " + text.charAt(0));
        System.out.println("Substring: " + text.substring(0, 5));
        System.out.println("Uppercase: " + text.toUpperCase());
        System.out.println("Lowercase: " + text.toLowerCase());
        System.out.println("Concat: " + text.concat("!"));
        System.out.println("Replace: " + text.replace("World", "Java"));
    }
}

public class StringCompare {
    public static void main(String[] args) {
        String str1 = "Hello";
        String str2 = "hello";
        String str3 = "Hello";

        System.out.println(str1.equals(str3));
        System.out.println(str1.equals(str2));
        System.out.println(str1.equalsIgnoreCase(str2));

        System.out.println(str1.contains("ell"));
        System.out.println(str1.startsWith("He"));
        System.out.println(str1.endsWith("lo"));
        System.out.println(str1.indexOf("l"));
    }
}

